INSERT INTO `stranky` (`list`, `poradi`, `nazev_lan1`, `obsah_lan1`, `aktiv_lan1`, `aktiv_lan1start`, `aktiv_lan1stop`, `keywords_lan1`, `nazev_lan2`, `obsah_lan2`, `aktiv_lan2`, `aktiv_lan2start`, `aktiv_lan2stop`, `keywords_lan2`, `nazev_lan3`, `obsah_lan3`, `aktiv_lan3`, `aktiv_lan3start`, `aktiv_lan3stop`, `keywords_lan3`, `aut_gen`, `editor`, `zmena`) VALUES('a0', 0, 'Vítejte na stránce plné živého umění a tvorby!', '<p style=\"text-align: center; font-size: 12pt;\"><a href=\"../index.php?list=s07&language=lan1&lay=\" target=\"_blank\"><img style=\"float: right; margin-bottom: 5px; margin-top: 10px;\" src=\"files/6209712.gif\" alt=\"německy\" width=\"30\" height=\"20\" /></a> <a href=\"https://www.facebook.com/otevrene.ateliery/?ref=aymt_homepage_panel\" target=\"_blank\"><img style=\"clear: both; margin-bottom: 5px; margin-top: 0px; float: right;\" src=\"files/1613257.jpg\" alt=\"na facebooku sdílet\" width=\"30\" height=\"30\" /></a></p>\r\n<p style=\"text-align: center; font-size: 12pt;\"><strong>7. ročník proběhne tradičně poslední víkend v září</strong></p>\r\n<p style=\"text-align: center; font-size: 12pt;\"><span style=\"font-size: 14pt;\"><strong> 24. - 25. 9. 2022</strong></span></p>\r\n<p style=\"text-align: center; font-size: 12pt; margin-top: 10px;\"><span style=\"font-size: 14pt;\"><strong><a class=\"odkaz_vtextu_kulaty_cerveny\" href=\"https://forms.gle/LguAjvezox5hi4nXA\" target=\"_blank\"><span style=\"font-size: 12pt;\"> Registrace pro umělce </span> </a></strong></span></p>\r\n<p> </p>\r\n<p style=\"text-align: center;\"><img src=\"files/1604707.jpg\" alt=\"oa1\" width=\"350\" height=\"300\" /> <img src=\"files/7933785.jpg\" alt=\"oa2\" width=\"190\" height=\"300\" /> <img src=\"files/1168137.jpg\" alt=\"tramvaj\" height=\"300\" /></p>\r\n<p style=\"text-align: center; font-size: 12pt;\">  </p>\r\n<p style=\"text-align: center; font-size: 12pt;\"> </p>\r\n<p style=\"text-align: center; font-size: 12pt;\"><span style=\"font-size: 10pt;\"><strong>Informace z 6. ročníku 2021</strong></span></p>\r\n<p style=\"text-align: center; font-size: 12pt;\"><span style=\"font-size: 10pt;\"><strong>Děkujeme, ze jste se chovali bezpečně!</strong></span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: 10pt;\"><a href=\"index.php?list=s18\" target=\"_blank\">Fotogalerie</a> | <a title=\"Tiskové zprávy\" href=\"index.php?list=s09\" target=\"_blank\">Tiskové zprávy</a></span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: 10pt;\"> </span></p>\r\n<div style=\"text-align: center;\"><span style=\"font-size: 10pt;\"> </span></div>\r\n<div style=\"text-align: center;\">\r\n<p style=\"text-align: center;\"><span style=\"font-size: 10pt;\"><strong>Pozvánka na 6.ročník</strong></span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: 10pt;\">--%VLOZVIDEOFLV_Otevrene_ateliery_pozvanka_2021%--<br /><br /></span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: 10pt;\"><strong>Na akci zve primátor města Plzně Mgr. Martin Baxa,<br />pod jehož záštitou se akce koná.</strong><br /></span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: 10pt;\">--%VLOZVIDEOFLV_Primator_mesta_Plzne_2021%--</span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: 10pt;\"> </span></p>\r\n</div>\r\n<div style=\"text-align: center;\"> </div>\r\n<div style=\"text-align: center;\"> </div>\r\n<div style=\"text-align: center;\"><strong><span style=\"font-size: 12pt;\">O AKCI</span></strong></div>\r\n<div style=\"text-align: center;\"> </div>\r\n<p style=\"float: left;\"><img style=\"margin: 5px 10px 5px 0;\" src=\"files/9240748.jpg\" alt=\"souteze\" /></p>\r\n<p><span style=\"font-size: 10pt;\"><strong><span style=\"font-size: 10pt;\">Návštěvníci objevují </span></strong>stanoviště <strong><a href=\"index.php?list=s05\" target=\"_blank\">v mapě</a></strong>, ve které je i spousta zeleně označených <strong>míst s tvořením</strong>. Kromě obdivování výstav tak mohou lidé zapojit své šikovné ruce a zkusit sochat, malovat, tvořit... <br /></span></p>\r\n<p> </p>\r\n<p><span style=\"font-size: 10pt;\">V rámci Ateliérů vozí návštěvníky<strong> zdarma umělecká tramvaj</strong>, která projíždí celou Plzní. Pro dospělé návštěvníky je i letos připravena <strong>vinná stezka ART&WINE (mapa vinné stezky).</strong></span></p>\r\n<p>K<span style=\"font-size: 10pt;\">aždým ročníkem vypisujeme <strong><a href=\"index.php?list=s13\" target=\"_blank\">soutěže</a> pro návštěvníky</strong> o zajímavé ceny. Nezapomeňte se podívat na pravidla, ať se můžete letos zapojit. Na obrázku vidíte loňskou vítěznou doručenou mapu s razítky ateliéristů - tento návštěvník obešel 27 stanovišť! A jaký rekord bude letos? :-)<br /></span></p>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>\r\n<p><span style=\"font-size: 10pt;\"><strong>Akce se koná za finanční podpory statutárního města Plzně, Plzeňského kraje a Nadace 700 let města Plzně, městských obvodů UMO1 a UMO3. Děkujeme za podporu i PMDP. </strong></span></p>\r\n<p> </p>\r\n<div style=\"float: left;\">\r\n<p style=\"text-align: left;\"><img src=\"files/8030856.png\" alt=\"pk\" width=\"158\" height=\"42\" /></p>\r\n<p> </p>\r\n<p><img style=\"margin-bottom: 10px;\" src=\"files/4028663.png\" alt=\"plz\" width=\"160\" height=\"43\" /></p>\r\n</div>\r\n<div style=\"display: inline-block;\"><img style=\"vertical-align: top; margin-bottom: 10px;\" src=\"files/7579948.jpg\" alt=\"umo3\" />    <img src=\"files/8831828.jpg\" alt=\"nadace700let\" height=\"100\" /><a style=\"margin-left: 10px; position: absolute;\" href=\"https://www.turisturaj.cz/\" target=\"_blank\"><img style=\"vertical-align: top;\" src=\"files/1976877.jpg\" alt=\"soubor turistuRaj.jpg\" width=\"170\" height=\"29\" /></a> <img style=\"margin-left: 10px;\" src=\"files/1654679.JPG\" alt=\"mestsky obvod 1\" width=\"170\" height=\"auto\" /></div>\r\n<p> </p>\r\n<p> </p>\r\n<p style=\"text-align: center;\"> </p>\r\n<p> </p>\r\n<p style=\"text-align: center;\"><strong><span style=\"font-size: small;\">Záštitu nad letošním ročníkem Víkendu otevřených ateliérů převzal <br />Mgr. Martin Baxa, primátor města Plzně</span></strong></p>\r\n<p> </p>\r\n<p> </p>\r\n<p style=\"text-align: center;\"><img src=\"files/7736271.jpg\" alt=\"záštita\" /></p>\r\n<p style=\"text-align: center;\"> </p>\r\n<div style=\"text-align: center;\"> </div>\r\n<div style=\"text-align: center;\"> </div>\r\n<div style=\"text-align: center;\"> </div>\r\n<div style=\"text-align: center;\"> </div>\r\n<p style=\"text-align: center;\"> </p>\r\n<p style=\"text-align: center;\"> <strong><span style=\"font-size: 10pt;\"><a href=\"https://www.facebook.com/otevrene.ateliery\" target=\"_blank\">Přidejte si nás na FB</a> a mějte informace z první ruky!</span></strong></p>\r\n<p style=\"text-align: center;\"> </p>\r\n<p style=\"text-align: center;\"> </p>\r\n<p> </p>', 1, '0000-00-00', '0000-00-00', 'otevřené ateliéry, plzeň, Plzeň tvořivá, radost, tvoření, ateliér', 'Welcome', '<p>  </p>', 1, '2007-08-30', '2007-08-30', 'Grafia, Pilsen, Plzeň, advertisement, promotion, courses, education, communication, PR, marketing, company communication, translation, interpreting, projects, EU projects, conference services', 'Herzlich willkommen!', '', 1, '2007-08-30', '2007-08-30', '', '0', 'kasl2405', '2022-02-02 08:43:44');
