INSERT INTO `stranky` (`list`, `poradi`, `nazev_lan1`, `obsah_lan1`, `aktiv_lan1`, `aktiv_lan1start`, `aktiv_lan1stop`, `keywords_lan1`, `nazev_lan2`, `obsah_lan2`, `aktiv_lan2`, `aktiv_lan2start`, `aktiv_lan2stop`, `keywords_lan2`, `nazev_lan3`, `obsah_lan3`, `aktiv_lan3`, `aktiv_lan3start`, `aktiv_lan3stop`, `keywords_lan3`, `aut_gen`, `editor`, `zmena`) VALUES('s11', 13, 'Organizátor Grafia, s.r.o.', '<p style=\"float: left;\"><a href=\"http://www.grafia.cz/\" target=\"_blank\"><img style=\"margin: 10px 7px 10px 0px;\" src=\"files/2070449.png\" alt=\"logo Grafia\" width=\"100\" height=\"70\" /></a></p>\r\n<p><span style=\"font-size: 12pt;\">Autorem myšlenky Víkendu otevřených ateliérů a organizátorem akce je <a title=\"prohlédněte si náš web!\" href=\"http://www.grafia.cz\" target=\"_blank\">Grafia, s.r.o.</a></span></p>\r\n<p><span style=\"font-size: 12pt;\">Umíme efektivně komunikovat akce našich zákazníků i ty vlastní, vzděláváme a zkoušíme dospělé, organizujeme eventy na klíč, vydáváme knihy... <br />Děláme to už od roku 1993 - rádi a dobře!</span></p>\r\n<p><span style=\"font-size: 12pt;\"> </span></p>\r\n<p><span style=\"font-size: 12pt;\">Budeme rádi za Vaše reakce či připomínky ke zlepšení: <br />pište na <a href=\"mailto:slukova@grafia.cz\">slukova@grafia.cz </a>nebo volejte 774 484 855</span></p>\r\n<p><span style=\"font-size: 12pt;\">Samozřejmě oceníme i pozitivní reakce ;-)</span></p>\r\n<p> </p>\r\n<p><span style=\"font-size: 12pt;\">A pokud byste chtěli zorganizovat nějakou akci, která má smysl a respektuje Vaši firemní kulturu, neváhejte kontaktovat naši produkční Ivetu Simonovou: <br /><a href=\"mailto:produkce@grafia.cz,\">produkce@grafia.cz,</a> 774 484 856</span></p>', 1, '2016-09-06', '2016-09-06', 'organizátor, grafia, autor akce, Plzeň, víkend otevřených ateliérů', 'Organizátor Grafia, s.r.o.', NULL, 0, NULL, NULL, NULL, 'Organizátor Grafia, s.r.o.', NULL, 0, NULL, NULL, NULL, '0', 'kasl2405', '2021-10-04 07:43:28');
INSERT INTO `stranky` (`list`, `poradi`, `nazev_lan1`, `obsah_lan1`, `aktiv_lan1`, `aktiv_lan1start`, `aktiv_lan1stop`, `keywords_lan1`, `nazev_lan2`, `obsah_lan2`, `aktiv_lan2`, `aktiv_lan2start`, `aktiv_lan2stop`, `keywords_lan2`, `nazev_lan3`, `obsah_lan3`, `aktiv_lan3`, `aktiv_lan3start`, `aktiv_lan3stop`, `keywords_lan3`, `aut_gen`, `editor`, `zmena`) VALUES('s13', 8, 'SOUTĚŽE pro návštěvníky', '<p> </p>\r\n<p><strong><span style=\"font-size: 12pt; color: #e2001a;\">VYHLÁŠENÍ SOUTĚŽÍ 6. ročníku Víkendu otevřených ateliérů </span></strong></p>\r\n<p> </p>\r\n<p><span style=\"font-size: 10pt;\"><strong> •</strong> <strong>Soutěžte s PMDP: Jakub Felt</strong>, <strong>Jan Kříž</strong><br />(výherce si cenu vyzvedne na pobočce PMDP - Denisovo nábřeží 920/12 v rámci provozní doby)</span></p>\r\n<p> </p>\r\n<p><span style=\"font-size: 10pt;\"><strong>•</strong> <strong>Soutěž o nejaktivnějšího návštěvníka: nevyhodnoceno</strong></span></p>\r\n<p><span style=\"font-size: 10pt;\"><strong>•</strong> <strong>Soutěž o nejhezčí snímky: Petra Kuthanová</strong></span></p>\r\n<p><strong><span style=\"font-size: 10pt;\">• Soutěž o nejhezčí video: Martin Bolda</span></strong></p>\r\n<p><strong><span style=\"font-size: 10pt;\">• Soutěž o nejobdivovanější umělecký výtvor: Jitka Kabátová</span></strong></p>\r\n<p> </p>\r\n<p> </p>\r\n<p><span style=\"font-size: 10pt;\">Výhry soutěží vyzvedněte <strong>v Grafii</strong> (Budilova 4, Plzeň) <strong>ve všední dny 8:00 - 15:00.</strong> Výhry, které nebudou vyzvednuté <strong>do 20. 12. 2021</strong>, propadnou. Děkujeme za účast :-).</span></p>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>\r\n<p><span style=\"color: #e2001a; font-size: 10pt;\"><strong>Pro návštěvníky jsme připravili tyto soutěže o atraktivní ceny:</strong></span></p>\r\n<p><span style=\"font-size: 10pt;\"> </span></p>\r\n<p><span style=\"font-size: 10pt;\"><strong>Soutěž o <span style=\"color: #e2001a;\">nejaktivnějšího návštěvníka</span> Víkendu otevřených ateliérů <br /></strong></span></p>\r\n<p><span style=\"font-size: 10pt;\">Hotel Plzeň odmění nejpilnějšího návštěvníka otevřených ateliérů. Vemte si na procházku po otevřených ateliérech<strong> tištěnou mapu ateliérů</strong> a nechte si ji při každé návštěvě od <strong>umělce orazítkovat</strong>. Orazítkovanou <strong>ji doručte do 10. 10. 2021</strong> na adresu pořadatele - Grafia s.r.o., Budilova 4, Plzeň. Toho, kdo získá nejvíce razítek, toho čeká odměna.<strong><br /></strong></span></p>\r\n<p><span style=\"font-size: 10pt;\"> <strong> </strong></span></p>\r\n<p><span style=\"font-size: 10pt;\"><strong>Soutěž o<span style=\"color: #e2001a;\"> nejhezčí snímky</span> z Víkendu otevřených ateliérů</strong></span></p>\r\n<p><span style=\"font-size: 10pt;\">Při svém putování po zastávkách v mapě <strong>foťte. Své</strong> snímky zašlete <strong>přes <a href=\"http://www.uschovna.cz\">www.uschovna.cz</a>  do 10. 10. 2021 </strong>na adresu <strong>ateliery@grafia.cz.</strong> Všechny snímky zveřejníme na facebooku a <strong>cenu získá autor</strong> nejzajímavějších snímků. </span></p>\r\n<p><span style=\"font-size: 10pt;\"> </span></p>\r\n<p><span style=\"font-size: 10pt;\"><strong>Soutěž o <span style=\"color: #e2001a;\">nejhezčí video</span> z Víkendu otevřených ateliérů</strong></span></p>\r\n<p><span style=\"font-size: 10pt;\">Při svém putování po zastávkách v mapě natáčejte.<strong> </strong>Svá videa zasílejte <strong>přes <a href=\"http://www.uschovna.cz\">www.uschovna.cz </a><span style=\"color: #000000;\">do 10<a style=\"color: #000000;\" href=\"http://www.uschovna.cz\">. 10. 202</a>1 </span></strong>na adresu <strong>ateliery@grafia.cz.</strong> Všechny videa zveřejníme na facebooku a cenu získá <strong>autor</strong> nejzajímavějšího videa.</span></p>\r\n<p><span style=\"font-size: 10pt;\">Video bude vybírat organizátor Víkendu otevřených ateliérů.</span></p>\r\n<p><span style=\"font-size: 10pt;\"> </span></p>\r\n<p><span style=\"font-size: 10pt;\"><strong>Soutěž o <span style=\"color: #e2001a;\">nejobdivovanější umělecký výtvor </span><span style=\"color: #000000;\">vytvořený návštěvníkem v rámci otevřených ateliérů (Výtvor, který dostane nejvíce \"lajků\".)</span><span style=\"color: #e2001a;\"><br /></span></strong></span></p>\r\n<p><span style=\"font-size: 10pt;\">Až se půjdete podívat do stanovišť, které nabízí možnost vyzkoušet si tvoření, pusťte se do toho! Tvořte, malujte, sochejte, atd., <strong>své dílo</strong> vyfoťte a sdílejte na facebooku s označením <strong>@Víkend otevřených ateliérů \"Plzeň tvořivá\"</strong>. Vyhraje příspěvek, který bude mít<strong> nejvíce \"lajků\" do 10. 10. 2021.</strong><br /></span></p>\r\n<p><span style=\"font-size: 10pt;\">POZOR! Zkontrolujte nastavení vašeho příspěvku, musí být nastavené na Veřejné. <a href=\"https://www.facebook.com/help/211513702214269/?helpref=related\" target=\"_blank\">Jak nastavit soukromí příspěvku? Klikněte.</a> </span></p>\r\n<p> </p>\r\n<p><img style=\"float: right; margin: 10px;\" src=\"files/9027156.jpg\" alt=\"pmdp\" width=\"298\" height=\"312\" /></p>\r\n<p> </p>\r\n<p> </p>\r\n<p><span style=\"color: #e2001a;\"><strong><span style=\"font-size: 10pt;\">Soutěžte s PMDP</span></strong></span></p>\r\n<p><span style=\"font-size: 10pt;\">Zašlete nám fotografii <strong>spojenou s městskou veřejnou dopravou v Plzni</strong> a zapojte se do soutěže. Ze zaslaných fotografií bude vybráno až <strong>5 výherců, kteří obdrží dárkový balíček od PMDP, a.s.</strong> Fotografie ve vysokém rozlišení zašlete <strong>do 10.10.2021</strong> na adresu <a href=\"mailto:ateliery@grafia.cz\">ateliery@grafia.cz</a> s předmětem<strong> FOTO PMDP</strong>. Lze poslat i přes <a href=\"http://www.uschovna.cz\" target=\"_blank\">www.uschovna.cz</a>. <br /><strong>Odesláním vyjadřujete souhlas</strong> se zveřejněním fotografií a případným bezplatným využitím Plzeňskými dopravními podniky.</span></p>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>', 1, '2016-09-23', '2016-09-23', 'Soutěže pro návštěvníky', 'Soutěže pro návštěvníky', NULL, 0, NULL, NULL, NULL, 'Soutěže pro návštěvníky', NULL, 0, NULL, NULL, NULL, '0', 'kasl2405', '2021-11-08 07:48:37');
INSERT INTO `stranky` (`list`, `poradi`, `nazev_lan1`, `obsah_lan1`, `aktiv_lan1`, `aktiv_lan1start`, `aktiv_lan1stop`, `keywords_lan1`, `nazev_lan2`, `obsah_lan2`, `aktiv_lan2`, `aktiv_lan2start`, `aktiv_lan2stop`, `keywords_lan2`, `nazev_lan3`, `obsah_lan3`, `aktiv_lan3`, `aktiv_lan3start`, `aktiv_lan3stop`, `keywords_lan3`, `aut_gen`, `editor`, `zmena`) VALUES('s14', 25, 'Rok 2016', '<h2><strong><span style=\"color: #e2001a;\">Jaký byl Víkend otevřených ateliérů v roce 2016?</span></strong></h2>\r\n<p><iframe src=\"https://www.facebook.com/plugins/video.php?href=https%3A%2F%2Fwww.facebook.com%2Fotevrene.ateliery%2Fvideos%2F310532772649995%2F&show_text=0&width=560\" width=\"560\" height=\"315\"></iframe></p>\r\n<p> </p>\r\n<h4 style=\"text-align: left;\">Jaký byl 1. ročník v roce 2016?</h4>\r\n<p style=\"text-align: left;\"> </p>\r\n<p><span style=\"font-size: 8pt;\"><strong><span style=\"color: #eb1b4c; font-family: verdana,geneva;\"><span style=\"color: #000000;\"><a style=\"color: #000000;\" title=\"Udělejte si netradiční víkend!\" href=\"../index.php?list=s33&language=lan1&lay=\" target=\"_blank\">První ročník Víkendu otevřených ateliérů \"Plzeň tvořivá\" </a></span></span></strong></span></p>\r\n<p style=\"text-align: left;\"><span style=\"color: #eb1b4c; font-size: 8pt; font-family: verdana,geneva;\"><strong><span style=\"color: #000000;\"><a style=\"color: #000000;\" title=\"Udělejte si netradiční víkend!\" href=\"../index.php?list=s33&language=lan1&lay=\" target=\"_blank\"> proběhl 24. - 25. 9. 2016.</a></span></strong></span></p>\r\n<p style=\"text-align: left;\"><span style=\"color: #eb1b4c; font-size: 8pt; font-family: verdana,geneva;\"><strong><span style=\"color: #000000;\">Účastnilo se ho na 50 vystavujících výtvarníků, v mapě bylo 30 zastávek s uměním a organizátoři spočítali cca 2000 návštěvníků. Děkujeme!</span></strong></span></p>\r\n<p style=\"text-align: left;\"> </p>\r\n<p style=\"text-align: left;\">Nejaktivnější návštěvník získal 21 razítek! Jmenuje se Milan Svoboda. Získává předplatné Plzeňského deníku. A protože cestou po ateliérech aktivně a dobře fotil, je i jedním z oceněných autorů <a title=\"soutěže pro návštěvníky\" href=\"../index.php?list=s13&language=lan1&lay=\" target=\"_blank\"><strong>fotosoutěže</strong></a>, kteří získali voucher na ubytování v hotelu IBIS Plzeň. Gratulujeme! </p>\r\n<p><img src=\"files/1774412.jpg\" alt=\"soubor Víkend otevřených ateliérů 25.9_orazítkovaná mapa malá.jpg\" /></p>\r\n<p style=\"text-align: left;\"> </p>\r\n<p style=\"text-align: left;\">Nadšení nad vlastní tvorbou z betonu v autobusové hale nebralo konce.  Vzniklo kolem padesáti soch!</p>\r\n<p style=\"text-align: left;\"> </p>\r\n<p><img src=\"files/8932565.jpg\" alt=\"soubor DSC_0991.jpg\" width=\"486\" height=\"734\" /></p>\r\n<p style=\"text-align: left;\"> </p>\r\n<p style=\"text-align: left;\"> </p>\r\n<p style=\"text-align: left;\">Pannenský porobeton a nářadí večer před akcí. Zítra z něj budou sochy! Zkusíte to?</p>\r\n<p><img src=\"files/1837042.jpg\" alt=\"soubor pripraveno pro sochání.jpg\" /></p>\r\n<p><img src=\"files/8133736.jpg\" alt=\"soubor nářadí_mala.jpg\" /></p>\r\n<p style=\"text-align: left;\"><span style=\"color: #e2001a; font-size: medium; font-family: verdana,geneva;\"><strong><span style=\"font-size: 14pt;\"> </span></strong></span></p>\r\n<p style=\"text-align: left;\"> </p>\r\n<p><iframe src=\"https://www.youtube.com/embed/3JpD17OnRlg?autoplay=0&rel=0&controls&origin=www.otevreneateliery.cz\" width=\"640\" height=\"380\"></iframe></p>\r\n<p style=\"text-align: left;\"> </p>\r\n<p style=\"text-align: left;\"><strong>Poprvé se na jeden víkend v Plzni otevřely veřejnosti ateliéry plzeňských výtvarných umělců</strong></p>\r\n<p> </p>\r\n<p style=\"text-align: left;\"><span style=\"color: #000000; font-size: 10pt; font-family: verdana,geneva;\"><span style=\"color: #eb1b4c;\">Celá akce byla pro návštěvníky i výtvarníky <strong>ZDARMA</strong></span></span></p>\r\n<p style=\"text-align: left;\"><span style=\"color: #000000; font-size: 10pt; font-family: verdana,geneva;\">Nad akcí  převzal <strong>záštitu náměstek primátora města Plzně Mgr. Martin Baxa</strong>.</span></p>\r\n<p> </p>\r\n<p><strong><span style=\"font-size: 10pt;\">Jaké výtvarníky uvidíte?</span></strong></p>\r\n<p style=\"text-align: left;\"><span style=\"color: #000000; font-size: 10pt; font-family: verdana,geneva;\">Malíře, kreslíře, sochaře, grafiky, šperkaře, oděvní návrháře, tatéra, keramika, architekty, fotografy, vedic art, designéry,tvůrce plastik...<br /></span></p>\r\n<p style=\"text-align: left;\"> </p>\r\n<p><span style=\"font-size: 10pt;\"><strong>Ateliéry otevřeli:</strong></span></p>\r\n<p><span style=\"font-size: 10pt;\">Milan Ďuriš</span></p>\r\n<p><span style=\"font-size: 10pt;\">Josef Haring</span></p>\r\n<p><span style=\"font-size: 10pt;\">Jana Holenková</span></p>\r\n<p><span style=\"font-size: 10pt;\">Renata Horáková</span></p>\r\n<p><span style=\"font-size: 10pt;\">Petra Horáková</span></p>\r\n<p><span style=\"font-size: 10pt;\">Helena Jarolímková</span></p>\r\n<p><span style=\"font-size: 10pt;\">Radovan Kodera</span></p>\r\n<p><span style=\"font-size: 10pt;\">Lukáš  Kubec</span></p>\r\n<p><span style=\"font-size: 10pt;\">Berenika Maněnová</span></p>\r\n<p><span style=\"font-size: 10pt;\">Blanka Piškulová</span></p>\r\n<p><span style=\"font-size: 10pt;\">Naděžda Potůčková</span></p>\r\n<p><span style=\"font-size: 10pt;\">Zdeněk Rolko</span></p>\r\n<p><span style=\"font-size: 10pt;\">Eva Roučka</span></p>\r\n<p><span style=\"font-size: 10pt;\">Alice Stehlíková</span></p>\r\n<p><span style=\"font-size: 10pt;\">Jana Škubalová</span></p>\r\n<p><span style=\"font-size: 10pt;\">Václav Šmolík</span></p>\r\n<p><span style=\"font-size: 10pt;\">Václav Šváb</span></p>\r\n<p><span style=\"font-size: 10pt;\">Benedikt Tolar</span></p>\r\n<p><span style=\"font-size: 10pt;\">Zuzana Uhlíková</span></p>\r\n<p><span style=\"font-size: 10pt;\">Luboš Vetengl</span></p>\r\n<p><span style=\"font-size: 10pt;\">Petr Vrobel</span></p>\r\n<p><span style=\"font-size: 10pt;\">Anna Wiener</span></p>\r\n<p><span style=\"font-size: 10pt;\"> </span></p>\r\n<p><span style=\"font-size: 10pt;\"><strong>V DEPU2015 vystavovali:</strong></span></p>\r\n<p><span style=\"font-size: 10pt;\">Jaroslav Hejl</span></p>\r\n<p><span style=\"font-size: 10pt;\">Roman Jindřich</span></p>\r\n<p><span style=\"font-size: 10pt;\">Olga Kezniklová</span></p>\r\n<p><span style=\"font-size: 10pt;\">Tatiana Klimenko</span></p>\r\n<p><span style=\"font-size: 10pt;\">Aneta Lišková</span></p>\r\n<p><span style=\"font-size: 10pt;\">Zuzana Lomičková</span></p>\r\n<p><span style=\"font-size: 10pt;\">Jan Mácha</span></p>\r\n<p><span style=\"font-size: 10pt;\">Kristýna Nová</span></p>\r\n<p><span style=\"font-size: 10pt;\">Jaroslav Opava</span></p>\r\n<p><span style=\"font-size: 10pt;\">Elena Pokaleva</span></p>\r\n<p><span style=\"font-size: 10pt;\">Pavlína Punčochářová</span></p>\r\n<p><span style=\"font-size: 10pt;\">Kristina Tenglerová</span></p>\r\n<p><span style=\"font-size: 10pt;\">Stanislava Topinková</span></p>\r\n<p><span style=\"font-size: 10pt;\">Marcela Urubová</span></p>\r\n<p><span style=\"font-size: 10pt;\">Veronika Volfová</span></p>\r\n<p><span style=\"font-size: 10pt;\">SUPŠ a ZUŠ Zámeček</span></p>\r\n<p> </p>\r\n<p><span style=\"font-size: 10pt;\"> </span></p>\r\n<p><span style=\"font-size: 10pt;\"><strong>Další zastávky s programem:</strong></span></p>\r\n<p><span style=\"font-size: 10pt;\">MOVING STATION</span></p>\r\n<p><span style=\"font-size: 10pt;\">ARTAMO restaurace - galerie</span></p>\r\n<p><span style=\"font-size: 10pt;\">Plzeň Zastávka</span></p>\r\n<p><span style=\"font-size: 10pt;\">Grafia</span></p>\r\n<p><span style=\"font-size: 10pt;\">SUPŠ a ZUŠ Zámeček</span></p>\r\n<p><span style=\"font-size: 10pt;\">Galerie Ladislava Sutnara / provozuje Fakulta designu a umění Ladislava Sutnara</span></p>\r\n<p><span style=\"font-size: 10pt;\">Il Mio Caffè</span></p>\r\n<p><span style=\"font-size: 10pt;\">Galerie města Plzně</span></p>\r\n<p><span style=\"font-size: 10pt;\">Západočeská galerie v Plzni, výstavní síň \"13\"</span></p>\r\n<p> </p>\r\n<p style=\"text-align: left;\">  <img src=\"files/1241411.jpg\" alt=\"soubor umo3.jpg\" /><span style=\"font-family: verdana,geneva; font-size: 8pt;\"><span id=\"ext-gen1401\">Konání akce bylo finančně podpořeno z rozpočtu <strong>MO Plzeň 3</strong></span> <span id=\"ext-gen1402\">z dotačního programu Podpora kultury, kulturních aktivit a oživení veřejného prostoru – „Místo pro život částkou\" 25.900 Kč.</span></span></p>\r\n<p style=\"text-align: left;\"> </p>', 1, '2017-06-02', '2017-06-02', '', 'Rok 2016', NULL, 0, NULL, NULL, NULL, 'Rok 2016', NULL, 0, NULL, NULL, NULL, '0', 'jab1408', '2021-10-04 07:43:28');
INSERT INTO `stranky` (`list`, `poradi`, `nazev_lan1`, `obsah_lan1`, `aktiv_lan1`, `aktiv_lan1start`, `aktiv_lan1stop`, `keywords_lan1`, `nazev_lan2`, `obsah_lan2`, `aktiv_lan2`, `aktiv_lan2start`, `aktiv_lan2stop`, `keywords_lan2`, `nazev_lan3`, `obsah_lan3`, `aktiv_lan3`, `aktiv_lan3start`, `aktiv_lan3stop`, `keywords_lan3`, `aut_gen`, `editor`, `zmena`) VALUES('s14_02', 2, 'Mapa', '<p><span style=\"font-size: 10pt;\"> </span></p>\r\n<p><a title=\"mapa ateliérů a dalších stanovišť\" href=\"https://mapy.cz/s/13ChT\" target=\"_blank\"><img src=\"files/4737584.jpg\" alt=\"mapa\" width=\"701\" height=\"432\" /></a></p>\r\n<p><span style=\"color: #000000; font-size: 10pt; font-family: verdana,geneva;\"><strong>Mapa bude průběžně aktualizována až do doby konání akce!</strong> </span></p>\r\n<p><span style=\"font-size: 10pt;\"> </span></p>\r\n<p><span style=\"font-size: 10pt;\">FINÁLNÍ MAPU otevřených ateliérů, galerií, výtvarných škol a dalších institucí najdete na tomto místě v polovině září.</span><br /> <br /><span style=\"font-size: 10pt;\"> Aktuální mapa <strong>zde</strong>: <a id=\"ext-gen2588\" title=\"https://mapy.cz/s/14PXN\" href=\"https://mapy.cz/s/14PXN\" target=\"_blank\">https://mapy.cz/s/14PXN</a></span></p>\r\n<p><span style=\"font-size: 10pt;\"> </span></p>\r\n<p><span style=\"font-size: 10pt;\"><strong>Tištěnou verzi mapy</strong> naleznete na infocentrech v Plzni po 15.9.</span></p>\r\n<p><span style=\"font-size: 10pt;\"> </span></p>\r\n<p><span style=\"font-size: 12pt;\">Nejsnadněji k mapě přijdete, když si koupíte <strong>Deník ve své okresní verzi</strong> (Plzeňský, Klatovský, Domažlický...)<strong> 23.9., kde bude její celostránková nejaktuálnější verze!</strong></span></p>\r\n<p> </p>\r\n<p><span style=\"font-size: 10pt;\"><strong>Zde také budou vyhlášeny ceny pro nejaktivnější návštěvníky.</strong></span></p>\r\n<p><span style=\"font-size: 10pt;\"><strong><br /><span style=\"font-size: 10pt;\"> A když už budete obcházet jednotlivé zastávky, nainstalujte si aplikaci do mobilu a POMÁHEJTE POHYBEM!</span><br /><span style=\"font-size: 10pt;\"> viz <a id=\"ext-gen1308\" class=\"mcntmoz-txt-link-freetext\" href=\"http://www.pomahejpohybem.cz/\" target=\"_blank\">http://www.pomahejpohybem.cz/</a></span></strong></span></p>\r\n<p> </p>\r\n<p> </p>', 1, '2017-06-02', '2017-06-02', 'mapa otevřených ateliérů', 'Mapa', NULL, 0, NULL, NULL, NULL, 'Mapa', NULL, 0, NULL, NULL, NULL, '0', 'administrator', '2017-06-02 14:40:46');
INSERT INTO `stranky` (`list`, `poradi`, `nazev_lan1`, `obsah_lan1`, `aktiv_lan1`, `aktiv_lan1start`, `aktiv_lan1stop`, `keywords_lan1`, `nazev_lan2`, `obsah_lan2`, `aktiv_lan2`, `aktiv_lan2start`, `aktiv_lan2stop`, `keywords_lan2`, `nazev_lan3`, `obsah_lan3`, `aktiv_lan3`, `aktiv_lan3start`, `aktiv_lan3stop`, `keywords_lan3`, `aut_gen`, `editor`, `zmena`) VALUES('s14_03', 3, 'Mapa a plánek ke stažení', '<p><span style=\"font-size: 12pt;\">Leták akce s mapou a přehledem umělců ve verzi .pdf</span></p>\r\n<p> </p>\r\n<p><span style=\"font-size: 14pt;\"><a class=\"odkaz_vtextu_kulaty_cerveny\" href=\"files/1307317.pdf\" target=\"_blank\">Mapa akce</a></span></p>\r\n<p> </p>\r\n<p><span style=\"font-size: 12pt;\">Plánek Depo 2015 s přehledem umělců ve verzi .pdf</span></p>\r\n<p> </p>\r\n<p><span style=\"font-size: 14pt;\"><a class=\"odkaz_vtextu_kulaty_cerveny\" href=\"files/1426961.pdf\" target=\"_blank\">Plánek Depo 2015</a></span></p>', 1, '2017-06-02', '2017-06-02', '', 'Mapa a plánek ke stažení', NULL, 0, NULL, NULL, NULL, 'Mapa a plánek ke stažení', NULL, 0, NULL, NULL, NULL, '0', 'administrator', '2017-06-02 14:40:46');
INSERT INTO `stranky` (`list`, `poradi`, `nazev_lan1`, `obsah_lan1`, `aktiv_lan1`, `aktiv_lan1start`, `aktiv_lan1stop`, `keywords_lan1`, `nazev_lan2`, `obsah_lan2`, `aktiv_lan2`, `aktiv_lan2start`, `aktiv_lan2stop`, `keywords_lan2`, `nazev_lan3`, `obsah_lan3`, `aktiv_lan3`, `aktiv_lan3start`, `aktiv_lan3stop`, `keywords_lan3`, `aut_gen`, `editor`, `zmena`) VALUES('s14_04', 4, 'Profily umělců', '<p> </p>\r\n<p><span style=\"font-size: 10pt;\"><span style=\"font-family: verdana, geneva;\">Zde budeme postupně zveřejňovat profily vystavujících umělců. <br /><br /></span></span></p>\r\n<p style=\"text-align: left;\"><span style=\"font-family: verdana, geneva; font-size: 10pt;\"><strong>Milan Ďuriš</strong> představuje svoji tvorbu a zve na Víkend otevřených ateliérů 24. - 25. 9. 2016 v Plzni:<br /></span></p>\r\n<p style=\"text-align: left;\"><iframe src=\"https://www.youtube.com/embed/3JpD17OnRlg?autoplay=0&rel=0&controls&origin=www.otevreneateliery.cz\" width=\"560\" height=\"315\"></iframe></p>\r\n<p> </p>\r\n<p style=\"text-align: left;\"><span style=\"font-size: 10pt;\">Také <strong>Luboš Vetengl</strong> přivítá návštěvníky během akce Víkend otevřených ateliérů:</span></p>\r\n<p style=\"text-align: left;\"><iframe src=\"https://www.youtube.com/embed/ND2BRb-IUoA?autoplay=0&rel=0&controls&origin=www.otevreneateliery.cz\" width=\"560\" height=\"315\"></iframe></p>\r\n<p> </p>\r\n<p><span style=\"font-size: 10pt;\">Jaké příběhy maluje na svá plátna <strong>Jaroslav Hejl</strong>? Zjistíte v dalším videomedailonku:</span></p>\r\n<p><iframe src=\"https://www.youtube.com/embed/HVr4DcS6yYg?autoplay=0&rel=0&controls&origin=www.otevreneateliery.cz\" width=\"560\" height=\"315\"></iframe></p>\r\n<p> </p>\r\n<p><span style=\"font-size: 10pt;\">V DEPU se budete moci podívat, jak se šijí módní kousky značky <strong>NEASI</strong>. <br />Na Víkend otevřených ateliérů zve nyní <strong>Aneta Lišková</strong>:</span></p>\r\n<p><iframe src=\"https://www.youtube.com/embed/6e4V18rMRlw?autoplay=0&rel=0&controls&origin=www.otevreneateliery.cz\" width=\"560\" height=\"315\"></iframe></p>\r\n<p> </p>\r\n<p><span style=\"font-size: 10pt; font-family: verdana,geneva;\"><strong>Naděžda Potůčková</strong>: keramička, malířka, kurátorka, ... Podívejte se na nové video a naplánujte si další zajímavou zastávku během Víkendu otevřených ateliérů 24. - 25. 9., tentokrát dokonce ve Zruči-Senci!</span></p>\r\n<p><iframe src=\"https://www.youtube.com/embed/QMFx_G_9Koo?autoplay=0&rel=0&controls&origin=www.otevreneateliery.cz\" width=\"560\" height=\"315\"></iframe></p>\r\n<p> </p>', 1, '2017-06-02', '2017-06-02', '', 'Profily umělců', NULL, 0, NULL, NULL, NULL, 'Profily umělců', NULL, 0, NULL, NULL, NULL, '0', 'administrator', '2017-06-02 14:40:46');
INSERT INTO `stranky` (`list`, `poradi`, `nazev_lan1`, `obsah_lan1`, `aktiv_lan1`, `aktiv_lan1start`, `aktiv_lan1stop`, `keywords_lan1`, `nazev_lan2`, `obsah_lan2`, `aktiv_lan2`, `aktiv_lan2start`, `aktiv_lan2stop`, `keywords_lan2`, `nazev_lan3`, `obsah_lan3`, `aktiv_lan3`, `aktiv_lan3start`, `aktiv_lan3stop`, `keywords_lan3`, `aut_gen`, `editor`, `zmena`) VALUES('s06', 11, 'Videogalerie', '<p><span style=\"font-size: 8pt;\"><strong><span style=\"color: #000000;\">Zveme Vás na otevřené ateliéry. </span></strong></span></p>\r\n<p><span style=\"font-size: 8pt;\"><strong><span style=\"color: #000000;\">Umělci a pan náměstek zvou širokou veřejnost do své \"kuchyně\". Sobota a neděle 23.- 24. 9. 2017</span></strong></span></p>\r\n<p> </p>\r\n<p><iframe src=\"https://www.youtube.com/embed/3DM8wOfl8gc?list=PLO5H97TNxyXifI6KHowAwbU0Xa9AC2Hq6\" width=\"350\" height=\"197\"></iframe> <iframe src=\"https://www.youtube.com/embed/dmGrEthBch4?list=PLO5H97TNxyXifI6KHowAwbU0Xa9AC2Hq6\" width=\"350\" height=\"197\"></iframe></p>\r\n<p> </p>\r\n<p><iframe src=\"https://www.youtube.com/embed/9SKi3e2ToQM?list=PLO5H97TNxyXifI6KHowAwbU0Xa9AC2Hq6\" width=\"350\" height=\"197\"></iframe> <iframe src=\"https://www.youtube.com/embed/19ptt7wqxwk?list=PLO5H97TNxyXifI6KHowAwbU0Xa9AC2Hq6\" width=\"350\" height=\"197\"></iframe></p>\r\n<p> </p>\r\n<p><iframe src=\"https://www.youtube.com/embed/qUxEdblso8Q?list=PLO5H97TNxyXifI6KHowAwbU0Xa9AC2Hq6\" width=\"350\" height=\"197\"></iframe> <iframe src=\"https://www.youtube.com/embed/VVRGD8lg4-4?list=PLO5H97TNxyXifI6KHowAwbU0Xa9AC2Hq6\" width=\"350\" height=\"197\"></iframe></p>\r\n<p><iframe src=\"https://www.youtube.com/embed/bZQkWpjao8k?rel=0\" width=\"350\" height=\"197\"></iframe></p>\r\n<p> </p>\r\n<p><span style=\"color: #e2001a;\"><strong>I Exodus Vás pozval na Víkend otevřených ateliérů a takto celou akci hodnotí:<br /></strong></span></p>\r\n<p>Jindřiška Rajzlová a Barbora Kmochová: \"Nejdříve bych Vám chtěla poděkovat za oslovení a že jste nás zařadili do tohoto projektu. Akce byla super, měli jsme workshop, zároveň jsme mohli představit široké veřejnosti naší sociální službu. Tím to zároveň byla velká reklama. Pomáhali i naši uživatelé. Návštěvníci nám psali milé vzkazy do kroniky a dle odhadu přišlo cca 100 lidí včetně dětí. Děti si mohly vybrat ze čtyř možností co si vytvořit. Pro maminky bylo připraveno jednoduché ruční šití. Co se vytvořilo, mohlo se vzít sebou. <br />Ještě jednou děkujeme a těšíme se na další spolupráci.\"</p>\r\n<p> </p>\r\n<p><iframe src=\"https://www.youtube.com/embed/FC2leYWk_tw\" width=\"350\" height=\"197\"></iframe></p>\r\n<p> </p>\r\n<p><span style=\"color: #e2001a;\"><strong>Vystavujte u sebe doma, na chodbě, ve sklepě, na střeše. Vše je možné. Ideální je místo kde tvoříte.</strong></span><br /><iframe src=\"https://www.youtube.com/embed/xw6AXLDbOwM?list=PLO5H97TNxyXifI6KHowAwbU0Xa9AC2Hq6\" width=\"350\" height=\"197\"></iframe><iframe src=\"https://www.youtube.com/embed/IQxcGL9Yxqs?list=PLO5H97TNxyXifI6KHowAwbU0Xa9AC2Hq6\" width=\"350\" height=\"197\"></iframe></p>\r\n<p><span style=\"color: #000000;\"> </span></p>\r\n<p><span style=\"color: #000000;\"><strong>Podívejme se spolu do Francie k Sophie Dressler, prezidentce podobné akce v Paříži, která vystavuje ve svém obýváku - ateliéru.</strong></span><br /><iframe src=\"https://www.youtube.com/embed/smgKcLK5B30?list=PLO5H97TNxyXifI6KHowAwbU0Xa9AC2Hq6\" width=\"350\" height=\"197\"></iframe><iframe src=\"https://www.youtube.com/embed/s3PvzYvIalo?list=PLO5H97TNxyXifI6KHowAwbU0Xa9AC2Hq6\" width=\"350\" height=\"197\"></iframe></p>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>\r\n<h1 style=\"text-align: center;\"><strong>VIDEOGALERIE Z ROKU 2016</strong></h1>\r\n<p> </p>\r\n<p><strong><span style=\"color: #e2001a;\">Jaký byl Víkend otevřených ateliérů v roce 2016?</span></strong></p>\r\n<p><iframe src=\"https://www.facebook.com/plugins/video.php?href=https%3A%2F%2Fwww.facebook.com%2Fotevrene.ateliery%2Fvideos%2F310532772649995%2F&show_text=0&width=560\" width=\"560\" height=\"315\"></iframe></p>\r\n<p><span style=\"color: #e2001a;\"><strong> </strong></span></p>\r\n<p> <strong>Ateliér Naděždy Potůčkové - keramička                        <span style=\"color: #e2001a;\">Módní značka NEASI: v roce 2017 již ve vlastním ateliéru</span></strong></p>\r\n<p><iframe src=\"https://www.facebook.com/plugins/video.php?href=https%3A%2F%2Fwww.facebook.com%2Fotevrene.ateliery%2Fvideos%2F306679213035351%2F&show_text=0&width=560\" width=\"350\" height=\"197\"></iframe> <iframe src=\"https://www.facebook.com/plugins/video.php?href=https%3A%2F%2Fwww.facebook.com%2Fotevrene.ateliery%2Fvideos%2F305285423174730%2F&show_text=0&width=560\" width=\"350\" height=\"197\"></iframe></p>\r\n<p> </p>\r\n<p><span style=\"color: #e2001a;\"><strong> Luboš Vetengl: kresba, malba, grafika                           <span style=\"color: #000000;\">Jaroslav Hejl: malíř</span></strong></span></p>\r\n<p><iframe src=\"https://www.facebook.com/plugins/video.php?href=https%3A%2F%2Fwww.facebook.com%2Fotevrene.ateliery%2Fvideos%2F303948516641754%2F&show_text=0&width=560\" width=\"350\" height=\"197\"></iframe><iframe src=\"https://www.facebook.com/plugins/video.php?href=https%3A%2F%2Fwww.facebook.com%2Fotevrene.ateliery%2Fvideos%2F303943539975585%2F&show_text=0&width=560\" width=\"350\" height=\"197\"></iframe></p>\r\n<p> </p>\r\n<p><strong> Milan Ďuriš - malíř</strong></p>\r\n<p><iframe src=\"https://www.facebook.com/plugins/video.php?href=https%3A%2F%2Fwww.facebook.com%2Fotevrene.ateliery%2Fvideos%2F301834170186522%2F&show_text=0&width=560\" width=\"350\" height=\"197\"></iframe></p>', 0, '2017-07-10', '2017-07-10', '', 'Videa', NULL, 0, NULL, NULL, NULL, 'Videa', NULL, 0, NULL, NULL, NULL, '0', 'jab1408', '2021-10-04 07:43:28');
