INSERT INTO `stranky` (`list`, `poradi`, `nazev_lan1`, `obsah_lan1`, `aktiv_lan1`, `aktiv_lan1start`, `aktiv_lan1stop`, `keywords_lan1`, `nazev_lan2`, `obsah_lan2`, `aktiv_lan2`, `aktiv_lan2start`, `aktiv_lan2stop`, `keywords_lan2`, `nazev_lan3`, `obsah_lan3`, `aktiv_lan3`, `aktiv_lan3start`, `aktiv_lan3stop`, `keywords_lan3`, `aut_gen`, `editor`, `zmena`) VALUES('s19', 24, 'Rok 2017', '<p style=\"text-align: left;\"><span style=\"font-size: 12pt;\"><strong>  </strong></span><span style=\"font-size: 10pt;\"><span style=\"text-align: left;\"><a title=\"Sdílete nás na Facebooku!\" href=\"https://www.facebook.com/otevrene.ateliery/?ref=aymt_homepage_panel\" target=\"_blank\"><img style=\"float: right;\" src=\"files/1904522.png\" alt=\"na facebooku sdílet\" width=\"173\" height=\"52\" /></a></span></span></p>\r\n<p><img src=\"files/6501584.jpg\" alt=\"soubor 20170924_181618_pro web.jpg\" width=\"776\" height=\"489\" /></p>\r\n<p style=\"text-align: left;\"> </p>\r\n<p style=\"text-align: left;\"><span style=\"font-size: 10pt;\"><strong><span style=\"color: #e2001a;\">Loňský ročník Víkendu otevřených ateliérů proběhl opět velmi úspěšně </span></strong></span><span style=\"color: #e2001a; font-size: 10pt;\"><span style=\"color: #eb1b4c; font-family: verdana, geneva;\"><span style=\"color: #000000;\">(23. - 24. září 2017)</span></span></span><span style=\"color: #e2001a;\"><span style=\"color: #eb1b4c; font-size: 12pt; font-family: verdana,geneva;\"><span style=\"color: #000000;\">.</span></span></span></p>\r\n<p style=\"text-align: left;\"><span style=\"font-size: 10pt;\"><span style=\"text-align: left;\"><a title=\"Sdílete nás na Facebooku!\" href=\"https://www.facebook.com/otevrene.ateliery/?ref=aymt_homepage_panel\" target=\"_blank\">Akce se zúčastnilo přes 5400 návštěvníků!</a></span></span></p>\r\n<p style=\"text-align: left;\"><span style=\"color: #eb1b4c; font-size: 10pt; font-family: verdana,geneva;\"><span style=\"color: #000000;\"><strong>Vystavovalo 82 umělců na </strong><strong>48 místech v Plzni a okolí!</strong></span></span></p>\r\n<p style=\"text-align: left;\"><span style=\"color: #eb1b4c; font-size: 10pt; font-family: verdana,geneva;\"><span style=\"color: #000000;\">Záštitu převzal <strong>Martin Baxa</strong>, náměstek hejtmana Plzeňského kraje a primátora města Plzně.<br /></span></span></p>\r\n<p style=\"text-align: left;\"> </p>\r\n<p style=\"text-align: left;\"><span style=\"color: #eb1b4c; font-size: 10pt; font-family: verdana,geneva;\"><span style=\"color: #000000;\">Množství fotek od Milana Svoboda najdete třeba <a title=\"Fotky Milan Svoboda 24.9.2017\" href=\"http://fotograf-milan-svoboda.rajce.idnes.cz/Vikend_otevrenych_atelieru_24.9.2017/\" target=\"_blank\">ZDE</a> a <a title=\"Fotky Milan Svoboda 23.9.2017\" href=\"http://fotograf-milan-svoboda.rajce.idnes.cz/Vikend_otevrenych_atelieru_23.9.2017/\" target=\"_blank\">ZDE</a>, od Vladimíra Čermáka <a title=\"Fotky VL. Čermák\" href=\"http://plzen.cz/fotogalerie/?gal=vikend-otevrenych-atelieru---foto-v.-cermak&id=284\" target=\"_blank\">ZDE</a></span></span></p>\r\n<p style=\"text-align: left;\"><span style=\"color: #eb1b4c; font-size: 10pt; font-family: verdana,geneva;\"><span style=\"color: #000000;\">Jak se tvořilo např. v DEPU uvidíte na videu <a title=\"VIDEO Víkend otevřených ateliérů\" href=\"https://www.facebook.com/otevrene.ateliery/?hc_ref=ARSNWQSn76yidc9HKOMdTkBDV_4gKBfzskMEt4p0QjSqeTu_waR9uFxJTIl5rw3Xnu8&fref=nf\" target=\"_blank\">ZDE</a><br /></span></span></p>\r\n<div style=\"float: right; margin-top: -20px; text-align: center;\"><a title=\"Mapa otevřených ateliérů v Google Maps\" href=\"https://drive.google.com/open?id=1904EF4JMo5Kp5_irRxVp9no_m7Q&usp=sharing\" target=\"_blank\"> <span style=\"font-size: 12pt; text-align: center;\">Interaktivní mapa:</span><br /> <img style=\"border: 2px solid #000; border-width: 2px; margin: 5px 10px 5px 10px;\" src=\"files/1242184.jpg\" alt=\"mapa otevřených ateliérů\" width=\"174\" height=\"116\" /></a>\r\n<p><span style=\"font-size: 10pt;\"><a href=\"index.php?list=download&file=1751790.pdf\" target=\"_blank\">Mapa v PDF ke stažení ZDE</a></span></p>\r\n</div>\r\n<p> </p>\r\n<p style=\"text-align: left;\"> </p>\r\n<p style=\"text-align: left;\"><strong><span style=\"color: #eb1b4c; font-size: 10pt; font-family: verdana,geneva;\"><span style=\"color: #000000;\">Mgr. Jana Brabcová, jednatelka Grafia s.r.o., zve na Víkend otevřených ateliérů.</span></span></strong></p>\r\n<p style=\"text-align: left;\"><span style=\"color: #eb1b4c; font-size: 10pt; font-family: verdana,geneva;\"><span style=\"color: #000000;\">--%VLOZVIDEOFLV_170919_StudioZ-Grafia-vikend otevrenych atelieru6%--</span></span></p>\r\n<p style=\"text-align: left;\"> </p>\r\n<p><span style=\"color: #eb1b4c; font-size: 10pt; font-family: verdana,geneva;\"><span style=\"color: #000000;\"><strong>Pozvánka na ročník 2017<br /></strong></span></span></p>\r\n<p>--%VLOZVIDEOFLV_Otevrene_ateliery_pozvanka2%--</p>\r\n<p> </p>\r\n<p><strong><span style=\"font-family: verdana,geneva; font-size: 10pt;\"><span style=\"color: #eb1b4c; font-size: 10pt; font-family: verdana,geneva;\"><span style=\"color: #000000;\"> Martin Baxa, náměstek hejtmana Plzeňského kraje a primátora města Plzně,</span></span> Vás zve na letošní ročník Víkendu otevřených ateliérů.</span><br /></strong></p>\r\n<p>--%VLOZVIDEOFLV_Baxa2%--</p>\r\n<p> </p>\r\n<p><strong>Videopozvánky umělců shlédněte <a class=\"odkaz_vtextu_kulaty_cerveny\" href=\"../index.php?list=s19_05\">  ZDE   </a>.</strong></p>\r\n<p> </p>\r\n<p style=\"text-align: left;\"><strong><strong id=\"mcntmcntmcntext-gen5542\">Akce se koná za finanční podpory statutárního města Plzně a Plzeňského kraje.  </strong></strong></p>\r\n<p style=\"text-align: right;\"><img style=\"float: left;\" src=\"files/8030856.png\" alt=\"pk\" width=\"158\" height=\"42\" /></p>\r\n<p style=\"text-align: right;\"><img style=\"float: left;\" src=\"files/4028663.png\" alt=\"plz\" width=\"160\" height=\"43\" /></p>\r\n<p style=\"text-align: left;\"> </p>\r\n<p style=\"text-align: left;\"> <strong><strong id=\"mcntmcntmcntext-gen5542\"><a href=\"http://www.turisturaj.cz\">www.turisturaj.cz</a>  </strong></strong></p>\r\n<p style=\"text-align: left;\"> </p>\r\n<p style=\"text-align: left;\"> </p>\r\n<h1 style=\"text-align: center;\"><strong><span style=\"color: #ffffff;\"><a style=\"color: #ffffff;\" href=\"../index.php?list=s05\">Aktuální mapu naleznete ZDE</a></span></strong></h1>\r\n<p style=\"text-align: left;\"> </p>\r\n<p><span style=\"color: #eb1b4c; font-size: 10pt; font-family: verdana,geneva;\"><strong><span style=\"color: #000000;\">Sledujte aktuality</span></strong></span> zde na webu, sledujte náš <a title=\"Víkend otevřených ateliérů na FB\" href=\"https://www.facebook.com/otevrene.ateliery\" target=\"_blank\">Facebook</a>!</p>\r\n<p><span style=\"color: #eb1b4c; font-size: 10pt; font-family: verdana,geneva;\"><strong><span style=\"color: #000000;\">Přidejte si nás na FB a mějte informace z první ruky!</span></strong></span></p>\r\n<p><strong><em><a href=\"https://www.facebook.com/otevrene.ateliery\">https://www.facebook.com/otevrene.ateliery</a></em></strong></p>\r\n<p style=\"text-align: left;\"> </p>\r\n<p style=\"margin-top: 30px;\">Děkujeme za spolupráci:<a title=\"Kudyznudy.cz - odkaz\" href=\"http://www.kudyznudy.cz/\" target=\"_blank\"><img style=\"margin-right: 20px; margin-left: 20px; vertical-align: middle;\" src=\"files/1059615.png\" alt=\"kudy z nudy\" width=\"182\" height=\"55\" /></a></p>\r\n<p> </p>\r\n<p> </p>\r\n<h4 style=\"text-align: left;\"><em>Jaký byl 1. ročník v roce 2016?</em></h4>\r\n<p style=\"text-align: left;\"> </p>\r\n<p style=\"text-align: left;\"><span style=\"color: #eb1b4c; font-size: 8pt; font-family: verdana,geneva;\"><strong><span style=\"color: #000000;\"><span style=\"color: #000000;\"><a style=\"color: #000000;\" title=\"Udělejte si netradiční víkend!\" href=\"../index.php?list=s33&language=lan1&lay=\" target=\"_blank\">První ročník Víkendu otevřených ateliérů \"Plzeň tvořivá\" </a></span><a style=\"color: #000000;\" title=\"Udělejte si netradiční víkend!\" href=\"../index.php?list=s33&language=lan1&lay=\" target=\"_blank\">proběhl 24. - 25. 9. 2016. </a></span></strong></span></p>\r\n<p style=\"text-align: left;\"><span style=\"color: #eb1b4c; font-size: 8pt; font-family: verdana,geneva;\"><strong><span style=\"color: #000000;\">Účastnilo se ho na 50 vystavujících výtvarníků, v mapě bylo 30 zastávek s uměním a organizátoři spočítali cca 2000 návštěvníků. Další info zde: <a title=\"Ročník 2016\" href=\"../index.php?list=s14&language=lan1&lay=\" target=\"_blank\">Ročník 2016</a><br /></span></strong></span></p>\r\n<p> </p>\r\n<p> </p>', 1, '2018-02-23', '2018-02-23', '', 'Rok 2017', NULL, 0, NULL, NULL, NULL, 'Rok 2017', NULL, 0, NULL, NULL, NULL, '0', 'administrator', '2021-10-04 07:43:28');
INSERT INTO `stranky` (`list`, `poradi`, `nazev_lan1`, `obsah_lan1`, `aktiv_lan1`, `aktiv_lan1start`, `aktiv_lan1stop`, `keywords_lan1`, `nazev_lan2`, `obsah_lan2`, `aktiv_lan2`, `aktiv_lan2start`, `aktiv_lan2stop`, `keywords_lan2`, `nazev_lan3`, `obsah_lan3`, `aktiv_lan3`, `aktiv_lan3start`, `aktiv_lan3stop`, `keywords_lan3`, `aut_gen`, `editor`, `zmena`) VALUES('s19_01', 1, 'Program 2017', '<p><strong>Galerie města Plzně:</strong><br />- vstup zdarma <br />„Maluj a vystavuj v galerii“<br />Milujete malování? Přijďte si namalovat opravdový obraz a zažijte, jak se cítí známý umělec. Vaše dílo zde vystavíme jako dílo slavného malíře.<br />Výtvarné potřeby budou zdarma v galerii.<br />Vstup do výtvarné dílny je z Dominikánské <br />ul. č.2 (14-20:00), vstup do výstavních prostor galerie je z nám. Republiky 40 (10-18:00) <br /><br /><strong>Ateliér Na střeše</strong><br />Posezení v ateliéru, promítání fotografií <br />a ukázky fotografování na střešní terase <br />a v podkrovním ateliéru <br />SO: Módní přehlídky s fotografováním – výjimečná módní designérka Marie Syrovátková <br />- vyzkoušejte si focení divadelního představení (divadelní soubor Klenba)<br />NE: Módní přehlídky s fotografováním – módní studio Zannia - Vyzkoušejte šaty z módního studia a nechte se v nich vyfotografovat, fotografii dostanete emailem<br /><br /><strong>SUPŠ a ZUŠ Zámeček</strong><br />Zámeček hned třikrát. Otevřené sochařské ateliéry v areálu radčického zámečku (Pod Vinicemi 82, Plzeň) i ateliéry v Křimicích (Zámecké nám. 9, Plzeň-Křimice). Ukázky studentských prací (výstavy) a zajímavé výtvarné workshopy. V ateliérech <br />v Křimicích můžete navštívit také promítání studentských filmů. Pásmo 30 krátkých filmů (smyčka). V DEPU2015 tvorba keramické mozaiky a výstava.<br />Obě budovy školy otevřeny od 14:00 hodin POUZE DO 18:00 HODIN!<br /><br /><strong>Výtvarno</strong><br />Kresba obličeje (busty) či malba zátiší. Pro děti malování zvířátek či dětských postav <br />a další motivy. Ukázky tvorby studentů. <br /><br /><strong>Sociálně terapeutická dílna Exodus</strong><br />Výroba drobností z textilu, papíru <br />a keramické hlíny (workshop SO i NE)<br /><br /><strong>Galerie Ladislava Sutnara</strong><br />otevřeno mimořádně v SO i NE 11 - 18h.<br />Výstava Současné izraelské umění - street <br />art a ilustrace.<br /><br /><strong>Západočeská galerie v Plzni</strong></p>\r\n<p>Ve výstavní síni Masné krámy můžete navštívit čerstvě otevřenou výstavu <em id=\"ext-gen1679\">Tenkrát v Evropě. Čeští umělci v totalitních režimech 1938–1953</em> </p>\r\n<p>(výstavní síň „13“ je momentálně uzavřena z důvodu instalace nové výstavy).<br />Obraz a komiks</p>\r\n<p>  - komiksová dílna</p>\r\n<p><span id=\"ext-gen1695\">  - <span id=\"ext-gen1694\">Dílnu vede vedoucí </span></span><span id=\"ext-gen1676\">Ateliéru Komiks a ilustrace pro děti na FDU ZČU doc. Bára Šalamounová</span><br />  - SO: 14 – 16.00 - pro věk: 8 – 15 let<br />  - NE: 14 – 16.00 - pro věk: 15 +<br />  - Místo: výstavní síň Masné krámy<br />Moje hračka v galerii <br />  - Přines oblíb. hračku a nech ji hrát ve filmu</p>\r\n<p>  - Dílnu vede lektorka Animánie Lenka Žalmanová<br />  - Věk: 7-15 let<br />  - Maximální počet účastníků: 15 <br />  - Tvůrčí dílna - digitální technika „stop motion“ – animace, při níž je reálný předmět mezi jednotl. snímky upravován <br />     a posouván o malé kousky,  aby vznikl dojem spojitosti (film)</p>\r\n<p>  - SO: 16-18.00<br />  - Místo: přednáškový sál ZČG, Pražská 13<br /><br /><strong>Ateliér Verry bižuterie</strong><br />Výroba doplňků z korálků (plast, dřevo, sklo). SO: v 15.00 a v 17.00 ukázky výroby šperků technikou křišťálová pryskyřice. <br /><br />Katedra výtvarné kultury, Pedagogická fakulta ZČU v Plzni<br />Pouze SO! <br />Výstava bak. a dipl. prací - fotografie, filmová tvorba, ale i média klasická, i práce z oblasti didaktiky výtvarné výchovy a výzkumu ve výtvarné výchově.<br /><br /><strong>Grafia</strong><br />- výstava a workshopy: <br />Kreslete vtipy s Vláďou Líbalem - SO 17:00, Tom Šolc - Jak se kreslí ženský akt?  <br />    - SO 14:00, NE 17:00 <br />Jaroslav Vogeltanz - promítání fotek dlouho-letého fotografa plzeňské ZOO a povídání na téma Jak fotit přírodu - NE 14:00  <br />Věra Karásková - SO i NE - výstava bižuterie + přijďte si zkusit speciální korálkovou techniku<br />Doporučujeme registraci na workshopy: grafia.cz nebo www.otevreneATELIERY.cz (počet míst omezen)<br /><br /><strong>Hotel Plzeň</strong><br />- výstava soch Vl. Palečka a fotek J. Noska<br />- zkuste si speciální techniku svařování soch <br />z kovu spolu se známým sochařem! <br />(průběžně SO i NE) <br /><br /><strong>R. Kodera a Z. Zbořilová</strong><br />Práce s klas. i dig. fotoaparáty v ateliéru i se zpracováním analog. fotografie ve fotokomoře. Při slunečném počasí pozorování náměstí Republiky s katedrálou <br />sv. Bartoloměje pomocí camery obscury. <br />V prostorách ateliéru a v podkroví výstava.<br /><br /><strong>Kulturka</strong><br />POUZE SO 23. 9. 14 - 20 h<br />Univerzitní co-working, kulturní klubovna<br />- workshop Automatická koláž pod vedením grafického designéra Jiřího Petričáka 16 -20:00 (NUTNÁ REZERVACE do 21. 9. kulturka@rek.zcu.cz či na 725 797 989). <br /><br /><strong>DEPO2015</strong><br />- výstava 25 umělců<br />- v každou celou hodinu komentované prohlídky všech prostor sdílené dílny Makerspace<br />- slavnostní otevření Řemeslného ateliéru<br />- zapojte se v prostoru MAKERSPACE do stavby dřevěného domku <br />- představení kurzů a řemeslných kroužků <br />- 24. 9. 14:00 – 19:00 - prezentace uměleckých rezidencí Ateliér OPEN A.i.R.: Seznamte se s Anne Erhard (Velká Británie/ Německo) -  ateliér, nově vzniklá fotokomora <br />- SO i NE: sochání z porobetonu a tvorba keramické mozaiky lektorují výtvarníci <br />ze Zámečku (materiál k dispozici na místě <br />+ nářadí pro 10 pracovišť, vhodné <br />pro jednotlivce i rodiny s dětmi), <br />rezervujte si místo předem na webu <br /> www.otevreneATELIERY.cz<br />- autorské čtení Václava Reithmaiera doprovází obrazy Zuzky Lomičkové<br />v SO od 14:30 a v NE od 18:00<br />- tvorba slunečních hodin - výklad a ukázky <br />K. Řezníčka - SO i NE od 14:30 a 16:00<br /><br /><strong>Václav Fiala  (KLATOVY)</strong><br />- galerie „BOX F4“;  Výstava “Jen tak“; Výstava fotografií “Tady“<br />SO od 16:00 - koncert Domažlické dudácké muziky<br /><br /><strong>Dům historie Přešticka</strong> <br />- výstava: keram. objekty Jana Hořkého, akvarely Jiřího Běla  <br />-NE: tvůrčí dílna  Vladimíry Písařové Živné <br />a ukázky malby akvarelem Jiřího Běla. <br />Dále také: Michaell Impellizzeri,  Jarka Papežová, Marie Soukupová, Hana Süssová<br />- výstava Fenomén Igráček a Naši hasiči<br /><br />Pestrý program proběhne i na dalších stanovištích. <br />POZOR, vyhlášena foto a video - soutěž pro návštěvníky!<br /><br /></p>', 1, '2018-02-23', '2018-02-23', '', 'Program 2017', NULL, 0, NULL, NULL, NULL, 'Program 2017', NULL, 0, NULL, NULL, NULL, '0', 'administrator', '2018-02-23 13:33:07');
INSERT INTO `stranky` (`list`, `poradi`, `nazev_lan1`, `obsah_lan1`, `aktiv_lan1`, `aktiv_lan1start`, `aktiv_lan1stop`, `keywords_lan1`, `nazev_lan2`, `obsah_lan2`, `aktiv_lan2`, `aktiv_lan2start`, `aktiv_lan2stop`, `keywords_lan2`, `nazev_lan3`, `obsah_lan3`, `aktiv_lan3`, `aktiv_lan3start`, `aktiv_lan3stop`, `keywords_lan3`, `aut_gen`, `editor`, `zmena`) VALUES('s19_07', 6, 'SOUTĚŽE pro návštěvníky', '<p><span style=\"color: #e2001a; font-size: 10pt;\"><strong>Pro návštěvníky byly připravené soutěže o atraktivní ceny</strong></span></p>\r\n<p><span style=\"font-size: 10pt;\"> </span></p>\r\n<p><span style=\"font-size: 10pt;\"><strong>Soutěž o <span style=\"color: #e2001a;\">nejaktivnějšího návštěvníka</span> Víkendu otevřených ateliérů <br /></strong></span></p>\r\n<p><span style=\"font-size: 10pt;\">Hotel Plzeň odmění nejpilnějšího návštěvníka otevřených ateliérů. Vemte si na procházku po otevřených ateliérech<strong> tištěnou mapu ateliérů</strong> a nechte si ji při každé návštěvě od <strong>umělce orazítkovat</strong>. Nejpozději <strong><span style=\"color: #e2001a;\">do 26. 9.</span> </strong>ji orazítkovanou doručte na adresu pořadatele - Grafia s.r.o., Budilova 4, Plzeň. Toho, kdo získá nejvíce razítek, toho <strong>Hotel Ibis</strong> a <strong>Hotel Plzeň***</strong> odmění <strong>poukazem do restaurace v hodnotě 500 Kč a 1000 Kč.</strong><strong><br /></strong></span></p>\r\n<p><span style=\"font-size: 10pt;\"><strong>Nejaktivnějšími návštěvníky se stali pan <span style=\"color: #e2001a;\">Milan Svoboda <span style=\"color: #000000;\">a pan</span> Tomáš Novák.</span></strong></span></p>\r\n<p><span style=\"font-size: 10pt;\"> <strong> </strong></span></p>\r\n<p><span style=\"font-size: 10pt;\"><strong>Soutěž o<span style=\"color: #e2001a;\"> nejhezčí snímky</span> z Víkendu otevřených ateliérů</strong></span></p>\r\n<p><span style=\"font-size: 10pt;\">Při svém putování po zastávkách v mapě <strong>foťte. Své</strong> snímky zašlete <strong>přes <a href=\"http://www.uschovna.cz\">www.uschovna.cz</a> </strong>na adresu <strong>ateliery@grafia.cz <span style=\"color: #e2001a;\">do 29. 9.</span></strong> Všechny snímky zveřejníme na facebooku a <strong>autor</strong> nejzajímavějších snímků získá <strong>víkendový pobyt pro dvě osoby se snídaní v Hotelu Plzeň***.</strong></span></p>\r\n<p><span style=\"font-size: 10pt;\"><strong>Nejhezčí snímky zaslala paní<span style=\"color: #e2001a;\"> Petra Bílá.</span></strong></span></p>\r\n<p><span style=\"font-size: 10pt;\"> </span></p>\r\n<p><span style=\"font-size: 10pt;\"><strong><span style=\"color: #e2001a;\">NOVINKA:</span> Soutěž o <span style=\"color: #e2001a;\">nejhezčí video</span> z Víkendu otevřených ateliérů</strong></span></p>\r\n<p><span style=\"font-size: 10pt;\">Při svém putování po zastávkách v mapě natáčejte.<strong> </strong>Svá videa zasílejte <strong>přes <a href=\"http://www.uschovna.cz\">www.uschovna.cz</a> </strong>na adresu <strong>ateliery@grafia.cz do 29. 9.</strong> Všechny videa zveřejníme na facebooku a <strong>autor</strong> nejzajímavějšího videa získá <strong>poukaz do restaurace hotelu Ibis v hodnotě 500 Kč.</strong></span></p>\r\n<p><span style=\"font-size: 10pt;\"><strong>Nejhezčí video zaslal pan <span style=\"color: #e2001a;\">Vladimír Čermák.</span></strong></span></p>\r\n<p><span style=\"font-size: 10pt;\"> </span></p>\r\n<p><span style=\"font-size: 10pt;\"><strong><span style=\"color: #e2001a;\">NOVINKA:</span> Soutěž o <span style=\"color: #e2001a;\">nejlepší návrh hotelového pokoje</span></strong></span></p>\r\n<p><span style=\"font-size: 10pt;\">Až se půjdete podívat do stanovišť, které jsou v Hotelu Plzeň na adrese Budilova 15, Plzeň, kousek od stanoviště v Grafii, prohlédněte si některé pokoje a navrhněte vlastní. <strong>Své návrhy</strong> zasílejte na adresu <strong>ateliery@grafia.cz do 26. 9. </strong>Nejlepší návrh získá <strong>víkendový pobyt pro dvě osoby se snídaní v Hotelu Plzeň***. </strong></span></p>\r\n<p><span style=\"font-size: 10pt;\"><strong>Výsledek nevyhlášen - nebyl odevzdán žádný návrh.<br /></strong></span></p>\r\n<p><span style=\"font-size: 10pt;\"> </span></p>\r\n<p><span style=\"font-size: 10pt;\"> </span></p>\r\n<h2><span style=\"color: #e2001a; font-size: 10pt;\">Jak se soutěžilo v roce 2016?</span></h2>\r\n<p><span style=\"font-size: 10pt;\"> </span></p>\r\n<p><span style=\"font-size: 10pt;\"><strong>3 vítězové soutěží byli:</strong></span></p>\r\n<p><span style=\"color: #e2001a; font-size: 10pt;\"><strong>Milan Svoboda</strong></span></p>\r\n<p><span style=\"color: #e2001a; font-size: 10pt;\"><strong>Šárka Kunešová</strong></span></p>\r\n<p><span style=\"font-size: 10pt;\"><strong><span style=\"color: #e2001a;\">Alibaba</span> </strong>(účastník je znám pouze pod přezdívkou z emailu)</span></p>\r\n<p><span style=\"font-size: 10pt;\"> </span></p>\r\n<p><span style=\"font-size: 10pt;\">Vítězům byl zaslán informační e-mail, jak a kde si vyzvednout výhru. Gratulujeme!</span></p>\r\n<p><span style=\"font-size: 10pt;\"> </span></p>\r\n<p><span style=\"font-size: 10pt;\"><strong>Zde jsou některé z nich:</strong></span></p>\r\n<p><span style=\"font-size: 10pt;\"> </span></p>\r\n<p><span style=\"font-size: 10pt;\"><img src=\"files/9428583.jpg\" alt=\"soubor DSC_1834.jpg\" width=\"567\" height=\"412\" /></span></p>\r\n<p><span style=\"font-size: 10pt;\">Foto Vladimír Křivka</span></p>\r\n<p><span style=\"font-size: 10pt;\"> </span></p>\r\n<p><span style=\"font-size: 10pt;\"><img src=\"files/1379354.jpg\" alt=\"soubor Anna Holá IMG_6587_mala.jpg\" /></span></p>\r\n<p><span style=\"font-size: 10pt;\">Foto Anna Holá</span></p>\r\n<p><span style=\"font-size: 10pt;\"> </span></p>\r\n<p><span style=\"font-size: 10pt;\"><img src=\"files/1559205.jpg\" alt=\"soubor AtelierNaStrese_fotoMirkaReifova_mala.jpg\" /></span></p>\r\n<p><span style=\"font-size: 10pt;\">Foto Mirka Reifová</span></p>\r\n<p><span style=\"font-size: 10pt;\"> </span></p>\r\n<p><span style=\"font-size: 10pt;\"