INSERT INTO `stranky` (`list`, `poradi`, `nazev_lan1`, `obsah_lan1`, `aktiv_lan1`, `aktiv_lan1start`, `aktiv_lan1stop`, `keywords_lan1`, `nazev_lan2`, `obsah_lan2`, `aktiv_lan2`, `aktiv_lan2start`, `aktiv_lan2stop`, `keywords_lan2`, `nazev_lan3`, `obsah_lan3`, `aktiv_lan3`, `aktiv_lan3start`, `aktiv_lan3stop`, `keywords_lan3`, `aut_gen`, `editor`, `zmena`) VALUES('s24_02', 0, 'Mapa', '<p style=\"text-align: left;\"><strong><span style=\"font-size: 12pt;\">Elektronická mapa 2020 </span><br /></strong></p>\r\n<p style=\"text-align: left;\"> </p>\r\n<p style=\"text-align: left;\"><span style=\"font-size: 10pt;\"><span style=\"color: #e2001a;\"><strong>K 9. 9. 2020 máme v mapě 62 zastávek v Plzni a v okolí Plzně! A více než 150 umělců.</strong></span><br /></span></p>\r\n<p style=\"text-align: left;\"><span style=\"color: #000000; font-size: 10pt;\">Klikněte na mapu a zobrazte si zastávky i s popiskami  </span></p>\r\n<p> </p>\r\n<p><span style=\"font-size: 10pt;\"><strong>Tištenou verzi mapy</strong> naleznete v galeriích a infostáncích v polovině září + v průběhu víkendu na jednotlivých místech, kde se tvoří nebo vystavuje. Ve středu 23.9. vyjde v mutaci Deníku pro Plzeňský kraj. Také si ji můžete <a href=\"index.php?list=s24_03\" target=\"_blank\">stáhnout na našich stránkách</a> a vytisknout sami už teď.</span></p>\r\n<p> </p>\r\n<p><span style=\"font-size: 10pt;\"><strong>Pozor:</strong> Bohužel kvůli Covidu někteří umělci nebudou vystavovat a některé body v mapě neotevřou:</span></p>\r\n<p><span style=\"font-size: 10pt;\"><strong>Nevystavují</strong>: Marcela Bryknerová, Alexandra Karlová, Julius Radič, Robin Stříbrný</span></p>\r\n<p><span style=\"font-size: 10pt;\"><strong>Neotevřou</strong>: </span></p>\r\n<p><span style=\"font-size: 10pt;\">11) Knihovna města Plzně </span></p>\r\n<p><span style=\"font-size: 10pt;\">24) Katedra výtvarné výchovy a kultury, FPE ZČU v Plzni</span></p>\r\n<p><span style=\"font-size: 10pt;\">28) Galerie THAMBOS </span></p>\r\n<p><span style=\"font-size: 10pt;\">33) DEPO 2015 </span></p>\r\n<p><span style=\"font-size: 10pt;\">57) Němejcův ateliér</span></p>\r\n<p><span style=\"font-size: 10pt;\">59) DARA - Raunerová Dana</span></p>\r\n<p><strong><span style=\"font-size: 10pt;\">Změny v otevírací době:</span></strong></p>\r\n<p><span style=\"font-size: 10pt;\">5) Terasa - otevřeno pouze v neděli</span></p>\r\n<p> </p>\r\n<p><strong><span style=\"font-size: 10pt;\">Seznam se průběžně doplňuje, než vyrazíte do vzdálenějších destinací, koukněte sem.</span></strong></p>\r\n<p> </p>\r\n<p style=\"text-align: left;\"><span style=\"font-size: 10pt;\"><a class=\"odkaz_vtextu_kulaty_cerveny\" href=\"https://www.google.com/maps/d/edit?mid=1_xCe2pfwXyhIbnjeyZSUUuVXbm-m7bOI&usp=sharing\" target=\"_blank\"> Podívat se na mapu otevřených ateliérů  </a></span></p>\r\n<p style=\"text-align: left;\"> </p>\r\n<p style=\"text-align: left;\"><span style=\"font-size: 10pt;\"><a class=\"odkaz_vtextu_kulaty_cerveny\" href=\"https://www.google.com/maps/d/edit?mid=1lVsR22W8g0t2qBjE3AUs0KhLSCCsxCHs&usp=sharing\" target=\"_blank\"> Podívat se na mapu vinné stezky  </a></span></p>\r\n<p> </p>\r\n<p><a href=\"https://www.google.com/maps/d/edit?mid=1_xCe2pfwXyhIbnjeyZSUUuVXbm-m7bOI&usp=sharing\" target=\"_blank\"><img src=\"files/1847829.png\" alt=\"soubor Mapa2.png\" width=\"830\" /></a></p>\r\n<p> </p>\r\n<p> </p>\r\n<p><span style=\"font-size: 10pt;\">A když už budete obcházet jednotlivé zastávky, nainstalujte si aplikaci do mobilu a POMÁHEJTE POHYBEM! <br />viz <a href=\"http://www.pomahejpohybem.cz/\">http://www.pomahejpohybem.cz/</a></span></p>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>', 1, '2021-04-20', '2021-04-20', '', 'Mapa', NULL, 0, NULL, NULL, NULL, 'Mapa', NULL, 0, NULL, NULL, NULL, '0', 'administrator', '2021-04-20 12:21:03');
